stage3 = new NGL.Stage(div_mol_3, { backgroundColor: "black",cameraType: "orthographic"});
smol3=await stage3.loadFile(molBlock3, {ext: "sdf", asTrajectory: false});
smol3.addRepresentation("licorice",{ "sele": "all", "color": "element"});
